% [Out,fn]=IsFile(fname,DName) % Out = 1 if file exists, 0 otherwise% filename returned in fn (so wild cards can be used)% If DName is specified it will get stuff from that directoryfunction[Out,fn]=IsFile(fname,DName)fname=char(fname);if(nargin<2)	s=dir(fname);	if(length(s)==0)		Out=0;		fn=[];	else		Out=1;		fn=char(s.name);	endelse    if(computer=='PCWIN') Sep='/';    else Sep=':';    end    dstr=[DName Sep fname];	s=dir(dstr);	if(length(s)==0)		Out=0;		fn=[];	else		Out=1;		Ds=[];		for i=1:length(s)			Ds=[Ds; [DName Sep]];		end				fn=[Ds [char(s.name)]];	endend