function LincButtonBoxDemoProgramsdirs=zeros(1,16);dirs(5:7)=1ActiveWire(1,'OpenDevice');ActiveWire(1,'SetDirection',dirs);ActiveWire(1,'SetPort',dirs);RedButton=2;YellowButton=3GreenButton=4;disp('All lights should be on \n');while(KbCheck); end; % wait until all keys are released.a=ActiveWire(1,'GetPort');  % wait until all buttons released;while(a(2)|a(3)|a(4))       a=ActiveWire(1,'GetPort'); end;RedPressedDown=0;YellowPressedDown=0;GreenPressedDown=0;tstart=getsecs;fprintf('Press any button\n\nPress any key to stop test\n')while(1);    a=ActiveWire(1,'GetPort');    as=a(RedButton);    if(a(RedButton))        if(~RedPressedDown);            fprintf(['Red button Pressed after ' num2str(getsecs-tstart) ' seconds!\n'])            fprintf('Press any button\n\nPress any key to stop test\n')            RedPressedDown=1;            tstart=getsecs;        end    else        RedPressedDown=0;    end    if(KBCheck)         disp('Finished!');        break;    endendactivewire('CloseAll')