function RTDemo(window)%window = SCREEN(0,'OpenWindow',WhiteIndex(0));white=WhiteIndex(window);black=BlackIndex(window);rect=SCREEN(window,'Rect');% Report the number and wait for responseAsk(window,'Hit enter or return to start the demo',black,white,'GetNumber');NumList=1:9;StrNum=int2str(NumList');ins=zeros(size(NumList));SCREEN(window,'TextSize',72);SCREEN(window,'TextFont','Times New Roman');[keyIsDown,secs,keyCode] = KbCheck;%for i=1:1000secs=0;count=1;getsecs;NotStillPressed=1;out=0;xp=rect(3)/2;yp=rect(4)/2;while(KbCheck) end;tstart=getsecs;tend=tstart+0.75;tstart=getsecs;SCREEN(window,'DrawText',StrNum(1,:),xp,yp,black);%tend=tstart+0.75;tend=getsecs+0.75;ins(1)=tstart;while (getsecs<=tend)   if(KbCheck)      if(NotStillPressed)            	         NotStillPressed=0;         out(count)=getsecs;         count=count+1;      end   else      NotStillPressed=1;   endendfor i=2:length(NumList)    SCREEN(window,'DrawText',StrNum(i-1,:),xp,yp,white);    if(KbCheck)        if(NotStillPressed)            	            NotStillPressed=0;            out(count)=getsecs;            count=count+1;        end    else        NotStillPressed=1;    end    SCREEN(window,'DrawText',StrNum(i,:),xp,yp,black);    tend=tend+0.75;	tend=getsecs+0.75;    	ins(i)=tstart;    while (getsecs<=tend)        if(KbCheck)            if(NotStillPressed)            		           NotStillPressed=0;   	           out(count)=getsecs;               count=count+1;            end        else            NotStillPressed=1;        end    endendtestend=9*0.75-getsecs+tstart;SCREEN(window,'DrawText',StrNum(i,:),xp,yp,white);