function ShowResultscd('C:\My Documents\RVIPData');[DummyFile,filenamelist]=isfile('*.dat');fprintf('Data files in this directory are:\n\n');for fnamecounter=1:size(filenamelist,1)   fprintf('  %d = %s\n',fnamecounter,filenamelist(fnamecounter,:));endfprintf('\n(if the list is too long, type <more on> before the <ShowResults>)\n');InputFlag=0;while(InputFlag==0)   userinput=input('\nEnter a number or the name of the data file without the .dat extension\n(eg for andy.dat type <andy>):\n','s');   Fn=[userinput '.dat'];   if(isfile(Fn))      InputFlag=1;   elseif(ismember(str2num(userinput),[1:length(filenamelist)]))      Fn=char(cellstr(filenamelist(str2num(userinput),:)));   end   if(isfile(Fn))      InputFlag=1;   else      fprintf('\nInvalid input\n');   end     end  DOut=load(Fn,'-ascii');figureh=subplot(1,3,1),bar(DOut(:,2)),title('Hits'),xlabel('Minutes'),axis tightsubplot(1,3,2),bar(DOut(:,3)),title('Misses'),xlabel('Minutes'),axis tightsubplot(1,3,3),errorbar(DOut(:,4),DOut(:,5)),title('Mean Reaction Times'),xlabel('Minutes'),axis tightDOut