function temptestt=getsecs;Waitsecs(0.35)getsecs-tfunction dummy        % this asks for and sttores a filename for the data file        fstr = GetEchoString(DrawingWin,'Enter filename:',300,400,black,white);        fname=[fstr TNumStr '.xls'];                   % this while loop checks to see if the file already exists. If it        % does, it asks whether it should overwrite and if not, prompts for        % a new filename        while(IsFile(fname))            YesStr = GetEchoString(DrawingWin,'Filename already used. Do you want to overwrite? [y/n]',300,400,black,white);            if(YesStr==y) break;             else fstr = GetEchoString(DrawingWin,'Enter a new filename:',300,400,black,white);            end            fname=[fstr '.xls']           end